export default function Carousel() {
  const [index, setIndex] = useState<number>(0);

  const nextSlide = () => {
    setIndex((prev) => (prev + 1) % carouselData.length);
  };

  const prevSlide = () => {
    setIndex((prev) => (prev - 1 + carouselData.length) % carouselData.length);
  };
//   Studybay anywhere with ExpertBuddy

  return (
    <div className="w-full flex flex-col items-center gap-4 p-6  my-16 rounded-lg">
         <h2 className="text-3xl font-bold text-gray-900 mb-4">
          <span className="relative inline-block top-[-10px] ">
            <CrownIcon className="absolute h-8 w-8 text-purple-600 -top-8 left-1/2 -translate-x-1/2" />
          </span>
          <span> Studybay  </span>anywhere with ExpertBuddy
        </h2>
      <div className="flex gap-4 overflow-hidden">
        {carouselData.slice(index, index + 2).map((item, idx) => (
          <motion.div
            key={idx}
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -50 }}
            transition={{ duration: 0.5 }}
          >
            <Card className="w-96 h-[220px] p-6 shadow-lg">
              <CardContent className="flex flex-col items-center text-center gap-3">
                {item.icon}
                <h3 className="text-lg font-semibold">{item.title}</h3>
                <p className="text-gray-600 ">{item.description}</p>
              </CardContent>
            </Card>
          </motion.div>
        ))}
      </div>

      <div className="flex gap-4">
        <Button onClick={prevSlide} variant="outline"><ArrowLeftFromLine  /></Button>
        <Button onClick={nextSlide} variant="outline"><ArrowRightFromLine /></Button>
      </div>
    </div>
  );
}
